CREATE SCHEMA IF NOT EXISTS `BS` DEFAULT CHARACTER SET utf8 ;
USE `BS` ;

-- -----------------------------------------------------
-- Table `BS`.`User`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`User` (
  `UID` INT NOT NULL AUTO_INCREMENT,
  `UUsername` VARCHAR(45) NULL,
  `UPass` VARCHAR(45) NULL,
  `UType` VARCHAR(45) NULL,
  PRIMARY KEY (`UID`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Patient`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Patient` (
  `PID` INT NOT NULL AUTO_INCREMENT,
  `PName` VARCHAR(45) NULL,
  `PAddress` VARCHAR(45) NULL,
  `PPhone` VARCHAR(45) NULL,
  `PStatus` VARCHAR(45) NULL,
  PRIMARY KEY (`PID`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Message`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Message` (
  `MID` INT NOT NULL AUTO_INCREMENT,
  `MTime` DATETIME NULL,
  `MMessage` TINYTEXT NULL,
  `MPhone` VARCHAR(45) NULL,
  `MRead` VARCHAR(45) NULL,
  `PID` INT NULL,
  PRIMARY KEY (`MID`),
  INDEX `fk_Message_Patient1_idx` (`PID` ASC),
  CONSTRAINT `fk_Message_Patient1`
    FOREIGN KEY (`PID`)
    REFERENCES `BS`.`Patient` (`PID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;



-- -----------------------------------------------------
-- Table `BS`.`Notes`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Notes` (
  `NID` INT NOT NULL AUTO_INCREMENT,
  `NTime` DATETIME NULL,
  `NNote` TINYTEXT NULL,
  `PID` INT NULL,
  PRIMARY KEY (`NID`),
  INDEX `fk_Notes_Patient1_idx` (`PID` ASC),
  CONSTRAINT `fk_Notes_Patient1`
    FOREIGN KEY (`PID`)
    REFERENCES `BS`.`Patient` (`PID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Medication`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Medication` (
  `MID` INT NOT NULL AUTO_INCREMENT,
  `MCategory` VARCHAR(45) NULL,
  `MName` VARCHAR(45) NULL,
  `MDescription` TEXT(3000) NULL,
  PRIMARY KEY (`MID`))
ENGINE = InnoDB;

-- -----------------------------------------------------
-- Table `BS`.`Prescription`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Prescription` (
  `PID` INT NOT NULL AUTO_INCREMENT,
  `PTime` DATETIME NULL,
  `Medication_MID` INT NOT NULL,
  `Patient_PID` INT NOT NULL,
  PRIMARY KEY (`PID`, `Medication_MID`, `Patient_PID`),
  INDEX `fk_Medication_has_Patient_Patient1_idx` (`Patient_PID` ASC),
  INDEX `fk_Medication_has_Patient_Medication_idx` (`Medication_MID` ASC),
  UNIQUE INDEX `PID_UNIQUE` (`PID` ASC),
  CONSTRAINT `fk_Medication_has_Patient_Medication`
    FOREIGN KEY (`Medication_MID`)
    REFERENCES `BS`.`Medication` (`MID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `fk_Medication_has_Patient_Patient1`
    FOREIGN KEY (`Patient_PID`)
    REFERENCES `BS`.`Patient` (`PID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Services`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Services` (
  `SID` INT NOT NULL AUTO_INCREMENT,
  `SDescription` VARCHAR(45) NULL,
  `SPrice` DECIMAL(6,2) NULL,
  PRIMARY KEY (`SID`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Bill`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Bill` (
  `BID` INT NOT NULL AUTO_INCREMENT,
  `BTime` DATETIME NULL,
  `BServices` VARCHAR(45) NULL,
  `BTotal` VARCHAR(45) NULL,
  `PID` INT NULL,
  `BStatus` VARCHAR(45) NULL,
  PRIMARY KEY (`BID`),
  INDEX `fk_Bill_Patient1_idx` (`PID` ASC),
  CONSTRAINT `fk_Bill_Patient1`
    FOREIGN KEY (`PID`)
    REFERENCES `BS`.`Patient` (`PID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Log`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Log` (
  `LID` INT NOT NULL AUTO_INCREMENT,
  `LTime` DATETIME NULL,
  `LUsername` VARCHAR(45) NULL,
  PRIMARY KEY (`LID`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `BS`.`Appoiment`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `BS`.`Appoiment` (
  `AID` INT NOT NULL AUTO_INCREMENT,
  `ANote` VARCHAR(45) NULL,
  `ADate` DATETIME NULL,
  `PID` INT NULL,
  PRIMARY KEY (`AID`),
  INDEX `fk_Appoiment_Patient1_idx` (`PID` ASC),
  CONSTRAINT `fk_Appoiment_Patient1`
    FOREIGN KEY (`PID`)
    REFERENCES `BS`.`Patient` (`PID`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;


